	/**
	 * 判断字符串是否为空
	 * 
	 * @param str 待判断字符串
	 * @return true：空；false：非空
	 */
	public static boolean isNull(String str) {
		return (str == null || str.trim().equalsIgnoreCase("") || str.trim().equalsIgnoreCase("null"));
	}

	/**
	 * 判断字符串是否为空
	 * @param obj 待判断对象
	 * @return true：空；false：非空
	 */
	public static boolean isNull(Object obj) {
		return (obj == null) || isNull(obj.toString());
	}

	public static String toString(Object value) {
		if (value != null) {
			return value.toString().trim();
		} else {
			return "";
		}
	}

	public static String NString(Object obj) {
		if (obj == null) {
			return "";
		}
		return obj.toString();
	}

	/**
	 * s[0]*31^(n-1) + s[1]*31^(n-2) + ... + s[n-1]
	 * </pre>
	 * </blockquote> using <code>int</code> arithmetic, where <code>s[i]</code> is
	 * the <i>i</i>th character of the string, <code>n</code> is the length of the
	 * string, and <code>^</code> indicates exponentiation. (The hash value of the
	 * empty string is zero.)
	 * @param beHashed
	 */
	public static long stringHash(String beHashed) {
		if (StringUtil.isEmpty(beHashed))
			return 0;
		else {
			int hash = 0;
			char[] strValue = beHashed.toCharArray();
			int len = strValue.length;
			for (int i = 0; i < len; i++)
				hash = hash * 31 + strValue[i];
			// a supplemental hash function
			hash += ~(hash << 9);
			hash ^= (hash >>> 14);
			hash += (hash << 4);
			hash ^= (hash >>> 10);
			return hash;
		}
	}

	public static String toStr(int intValue) {
		return String.valueOf(intValue);
	}

	public static String toStr(double d) {
		return String.valueOf(d);
	}

	public static String toStr(long d) {
		return String.valueOf(d);
	}

	/**
	 * 判断是否字符串等于给定字符数组中的一个.
	 * @param str
	 * @param arg
	 * @return
	 */
	public static boolean equals(String str, String[] arg) {
		if (str == null || arg == null)
			return false;

		for (int i = 0; i < arg.length; i++) {
			if (str.equals(arg[i]))
				return true;
		}
		return false;
	}

	/**
	 * 判断是否字符串等于给定字符数组中的一个,忽略大小写.
	 * @param str
	 * @param arg
	 * @return
	 */
	public static boolean equalsIgnoreCase(String str, String[] arg) {
		if (str == null || arg == null)
			return false;

		for (int i = 0; i < arg.length; i++) {
			if (str.equalsIgnoreCase(arg[i]))
				return true;
		}
		return false;
	}

  /**
   * 生成MD5
   * @param originMsg
   * @return
   */
  public synchronized static String genMD5(String originMsg) {
    md5.Init();
    md5.Update(originMsg);
    return md5.asHex();
  }

	/**
	 * 生成uuid
	 */
	public static String createUUID8() {
		StringBuffer shortBuffer = new StringBuffer();
		String uuid = UUID.randomUUID().toString().replace("-", "");
		for (int i = 0; i < 8; ++i) {
			String str = uuid.substring(i * 4, i * 4 + 4);
			int x = Integer.parseInt(str, 16);
			shortBuffer.append(chars[(x % 62)]);
		}
		return shortBuffer.toString();
	}

	public static String inSql(String field, List<Map<String,String>> list){
		StringBuffer sql = new StringBuffer(" and ");
		sql.append(field);
		sql.append(" in(");
		for(int i=0;i<list.size();i++){
			Map<String,String> stringMap = list.get(i);
			sql.append(stringMap.get(field));
			if(i!=list.size()-1){
				sql.append(",");
			}
		}
		sql.append(")");
		return sql.toString();
	}

	public static String inSql(String filed,String exp){
		StringBuffer sql = new StringBuffer();
		sql.append(" (");
		List<Long> ids = resourceIdSplit(exp);
		List<List<Long>> partition = Lists.partition(ids,800);
		for(int i = 0 ;i<partition.size();i++){
			sql.append(convertToExpressStr(partition.get(i),filed));
			if(i<partition.size()-1){
				sql.append(" or ");
			}
		}
		return sql.append(" ) ").toString();
	}

	public static List<Long> resourceIdSplit(String exp){
		List<Long> resourceIds = new ArrayList<>();
		if(exp.contains("(")){
			String [] split = exp.substring(exp.indexOf("(")+1,exp.indexOf(")")).split(",");
			for(String id:split){
				resourceIds.add(Long.valueOf(id));
			}
		}
		return resourceIds;
	}

	public static  String convertToExpressStr(List<Long> ids, String field) {
		return field + " in (" + StringUtils.join(ids, ",") + ")";
	}

